import ProfileDetails from "./ProfileDetails";
import AboutMe from "./AboutMe";
import { Card, Container, Row } from "react-bootstrap";
import React, { useState } from "react";
import Service from "./Service";
import styled from "styled-components";
import Services from "../../constants/services";

const Avatar = styled.img`
  border-radius: 100%;
  height: 100px;
  margin: auto;
  margin-left: 30px;
  width: 100px;
`;

const StyledCard = styled(Card)`
  width: 100%;
  margin: 1rem auto;
`;

const Details = styled(StyledCard)`
  display: grid;
  grid-template-columns: 1fr auto 1fr;
`;

const ServicesCard = styled(StyledCard)`
  display: grid;
  width: 100%;
  margin: 1rem auto;
`;

const About = styled(StyledCard)``;

const Portfolio = styled(StyledCard)``;

function EditProfile() {
  return (
    <Container>
      <Details>
        <Avatar src="#" />
        <ProfileDetails />
      </Details>
      <About>
        <AboutMe />
      </About>
      <ServicesCard>
        <div>
          {Services.map(service => (
            <Service
              key={service.key}
              image={service.image}
              name={service.name}
              count={service.count}
            />
          ))}
        </div>
      </ServicesCard>
      <Portfolio>
        <h1>Portfolio</h1>
      </Portfolio>
    </Container>
  );
}

export default EditProfile;
