import { Button } from "react-bootstrap";
import React from "react";
import styled from "styled-components";
import { useTranslation } from "react-i18next";

const Wrapper = styled.div`
  cursor: pointer;
  position: relative;
  width: 100%;
  height: 0;
  padding-bottom: 100%;
`;

const Image = styled.img`
  display: block;
  width: 100%;
`;

const Name = styled.span`
  display: block;
  position: absolute;
  top: 1rem;
  left: 1rem;
  color: #fff;
  font-size: 1.125rem;
  font-weight: 500;
`;
const Count = styled.span`
  display: block;
  position: absolute;
  bottom: 1rem;
  left: 1rem;
  color: #fff;
  font-size: 0.625rem;
`;
const ButtonWrapper = styled.div`
  display: block;
  position: absolute;
  bottom: 1rem;
  right: 1rem;
  color: #fff;
`;


function ServiceImage({ name, count, checked, image, keyName, onButtonClick }) {

  const { t } = useTranslation();
  return (
    <Wrapper>
      <Image src={image} />
      <Name>{name}</Name>
  <Count>{count} {t("auth.signUpDetails.serviceImage.people")}</Count>
      <ButtonWrapper>
        <Button
          name={keyName}
          onClick={onButtonClick}
          type="button"
          size="sm"
          variant="light"
        >
          {checked ? "Remove" : "Add"}
        </Button>
      </ButtonWrapper>
    </Wrapper>
  );
}

export default ServiceImage;
