import styled from "styled-components";

const Toggle = styled.div`
  cursor: pointer;
  margin: 1rem 0;
  font-size: 0.8125rem;
  font-weight: 700;
  display: grid;
  grid-template-columns: 1fr auto;

  .arrow {
    transform: translateY(7px) rotate(0deg);
    &:not(.invert) {
      transform: translateY(7px) rotate(180deg);
    }
  }
`;

export default Toggle;
