import {
  BREAK_POINT_SMALLEST_SCREEN,
  CURRENCY_DAI,
  CURRENCY_ETH
} from "./constants.js";
import {
  Button as BootstrapButton,
  OverlayTrigger,
  Tooltip
} from "react-bootstrap";

import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  padding-right: 40px;
  position: relative;
  text-align: right;

  @media (max-width: ${BREAK_POINT_SMALLEST_SCREEN}) {
    padding-right: 0;
  }
`;

const Button = styled(BootstrapButton)`
  appearance: none;
  background: none;
  border: none;
  margin: 0;
  outline: none;
  padding: 0;
  text-decoration: underline;
  color: #051441;
  & > .h5 {
    font-weight: 600;
  }
`;

const renderCurrencyTooltip = (props, currency) => {
  const alternateCurrency =
    currency === CURRENCY_DAI ? CURRENCY_ETH : CURRENCY_DAI;
  return (
    <Tooltip {...props} show={null} id="currency-hover-tooltip">
      Click or to view {alternateCurrency}
    </Tooltip>
  );
};

function WalletCurrency({ amount, currency, onToggleCurrency }) {
  return (
    <Wrapper className="currency-wrapper">
      <OverlayTrigger
        placement="bottom"
        overlay={props => renderCurrencyTooltip(props, currency)}
        trigger="hover"
      >
        <Button className="h5" variant="link" onClick={onToggleCurrency}>
          <span className="h5">
            {amount} {currency}
          </span>
        </Button>
      </OverlayTrigger>
    </Wrapper>
  );
}

export default WalletCurrency;
