import { Button, Card, Col, Container, Row } from "react-bootstrap";
import { CURRENCY_DAI, CURRENCY_ETH } from "./constants.js";
import React, { useState } from "react";

import SendForm from "./SendForm";
import Transaction from "./Transaction";
import WalletCurrency from "./WalletCurrency";
import WalletID from "./WalletID";
import styled from "styled-components";

const CardHeader = styled(Card.Title)`
  display: grid;
  grid-template-columns: auto 1fr auto;
  @media (max-width: 400px) {
    grid-template-columns: auto;
  }
`;
const CardTitle = styled.span`
  display: block;
`;

// Placeholder - this will ultimate come from the API
const defaultTransactions = [
  {
    id: 123,
    userAvatar: "https://i.pravatar.cc/100",
    userName: "Craig Siphron",
    date: new Date(),
    amount: "3.1213 ETH",
    sent: true // NOTE this indicates whethere i sent or received the amount. This is probably the wrong term.
  },
  {
    id: 456,
    userAvatar: "https://i.pravatar.cc/100",
    userName: "Craig Siphron",
    date: new Date(),
    amount: "3.1213 ETH",
    sent: false // NOTE this indicates whethere i sent or received the amount. This is probably the wrong term.
  }
];

function Wallet() {
  const [transactions] = useState(defaultTransactions);
  const [viewCurrency, setViewCurrency] = useState(CURRENCY_DAI);
  const [walletCurrency, setWalletCurrency] = useState(CURRENCY_ETH);
  const [amount, setAmount] = useState(100.1234);

  const handleToggleCurrency = () => {
    setViewCurrency(
      viewCurrency === CURRENCY_DAI ? CURRENCY_ETH : CURRENCY_DAI
    );
    setAmount(amount + 100);
  };

  return (
    <Card>
      <Card.Body>
        <CardHeader>
          <CardTitle>Home Wallet</CardTitle>
          <WalletID walletID="1Fm9gVZynQvjecVUD6GuCyiR24uoWi2JK6" />
          <WalletCurrency
            amount={amount}
            currency={viewCurrency}
            onToggleCurrency={handleToggleCurrency}
          />
        </CardHeader>
        <Container>
          <Row>
            <Col md={1} lg={2} className="d-none d-md-flex"></Col>
            <Col md={10} lg={8}>
              <SendForm currency={walletCurrency} />
              <hr className="mt-4 mb-4" />
              <div>
                {transactions.map(transaction => (
                  <Transaction
                    key={transaction.id}
                    userAvatar={transaction.userAvatar}
                    userName={transaction.userName}
                    date={transaction.date}
                    amount={transaction.amount}
                    sent={transaction.sent}
                  />
                ))}
              </div>
              <Button block variant="link" className="text-muted">
                <small>More transactions</small>
              </Button>
            </Col>
            <Col md={1} lg={2} className="d-none d-md-flex"></Col>
          </Row>
        </Container>
      </Card.Body>
    </Card>
  );
}

export default Wallet;
