import { Container, Nav, Navbar } from "react-bootstrap";
import React, { useState } from "react";

import Avatar from "SharedComponents/Avatar";
import ChatIcon from "images/chat.svg";
import CloseMenuIcon from "images/closeMenu.svg";
import ContractIcon from "images/contract.svg";
import { Link } from "react-router-dom";
import Logo from "images/logo.svg";
import MenuIcon from "images/menu.svg";
import SearchIcon from "images/search.svg";
import WalletIcon from "./WalletIcon";
import styled from "styled-components";

const Wrapper = styled(Container)`
  max-width: 1200px;
`;

const Brand = styled(Link)`
  flex-grow: 999;
`;

function RiseNav() {
  const [ expanded, setExpanded ] = useState(false);

  const showMenu = () => setExpanded(true)
  const hideMenu = () => setExpanded(false)

  return (
    <Navbar expand="lg" expanded={expanded}>
      <Wrapper>
        <Navbar.Brand as={Brand} to="/">
          <img src={Logo} alt="Home"/>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" onClick={showMenu}>
          <img src={MenuIcon} alt="Show menu" />
        </Navbar.Toggle>
        <Navbar.Collapse id="basic-navbar-nav">
          <Navbar.Toggle
            aria-controls="basic-navbar-nav"
            className="close-menu d-lg-none"
            onClick={hideMenu}
          >
            <img src={CloseMenuIcon} alt="Close menu" />
          </Navbar.Toggle>
          <Nav className="mr-auto">
            <Nav.Link
              as={Link}
              to="/account"
              className="pl-auto order-lg-5 account"
              onClick={hideMenu}
            >
              <span className="d-lg-none account-user-name">Justin Smith</span>
              <Avatar
                name="Justin Smith"
                size={64}
                className="account-avatar"
              />
            </Nav.Link>
            <Nav.Link href="/home" className="d-lg-none" onClick={hideMenu}>
              Home
            </Nav.Link>
            <Nav.Link
              href="/search"
              className="order-lg-0 d-none d-lg-inline"
              onClick={hideMenu}
            >
              <img src={SearchIcon} alt="Search" className="d-none d-lg-inline " />
              <span className="d-none d-lg-none">Search</span>
            </Nav.Link>
            <Nav.Link
              as={Link}
              to="/contracts"
              className="order-lg-2"
              onClick={hideMenu}
            >
              <img
                src={ContractIcon}
                alt="Contracts"
                className="d-none d-lg-inline"
              />
              <span className="d-lg-none">Contracts</span>
            </Nav.Link>
            <Nav.Link
              as={Link}
              to="/project"
              className="order-lg-1"
              onClick={hideMenu}
            >
              <img
                src={ChatIcon}
                alt="Projects"
                className="d-none d-lg-inline"
              />
              <span className="d-lg-none">Projects</span>
            </Nav.Link>
            <Nav.Link
              as={Link}
              to="/wallet"
              className="order-lg-3 d-none d-lg-inline"
              onClick={hideMenu}
            >
              <WalletIcon />
              <span className="d-lg-none">Wallet</span>
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Wrapper>
    </Navbar>
  );
}

export default RiseNav;
