import { Button, Card, Col, Container, Form, Row } from "react-bootstrap";
import React, { useState } from "react";

import styled from "styled-components";
import { useTranslation } from "react-i18next";

const TagWrapper = styled.div`
  position: absolute;
  top: -38px;
  left: 0;
  display: flex;
`;

const Tag = styled.div`
  position: relative;
  background: #e9ebf4;
  padding: 2px calc(1rem + 20px) 2px 1rem;
  border-radius: 14px;
  margin-right: 4px;
`;

const TagDeleteButton = styled.button`
  position: absolute;
  right: 12px;
  top: 3px;
  appearance: none;
  background: none;
  border: none;
  outline: none;
  padding: 0;
  margin: 0;
`;

function SignUpAuthorDetails({
  defaultValues = { services: [], location: "" },
  onBackClick,
  onSubmit
}) {
  const DEFAULT_FORM = {
    services: defaultValues.services,
    currentService: "",
    location: defaultValues.location
  };

  const [form, setForm] = useState(DEFAULT_FORM);
  const { t } = useTranslation();

  const getSubmitPayload = () => {
    return {
      location: form.location,
      services: form.services
    };
  };

  const handleSubmit = e => {
    e.preventDefault();
    onSubmit(getSubmitPayload());
  };

  const handleBackClick = () => {
    onBackClick(getSubmitPayload());
  };

  const handleChange = e => {
    setForm({
      ...form,
      [e.target.name]: e.target.value || ""
    });
  };

  const handleServicesBlur = e => {
    const {
      target: { value }
    } = e;
    setForm({
      ...form,
      services: [...form.services, value.replace(",", "")],
      currentService: ""
    });
    return;
  };

  const handleServicesChange = e => {
    const {
      target: { value }
    } = e;
    if (value.match(",")) {
      setForm({
        ...form,
        services: [...form.services, value.replace(",", "")],
        currentService: ""
      });
      return;
    }
    setForm({
      ...form,
      currentService: value
    });
  };

  const handleTagDeleteClick = removeIndex => {
    setForm({
      ...form,
      services: form.services.filter((_, i) => i !== removeIndex)
    });
  };

  const isFormValid = () => {
    if (!form.location || !form.location.length) return false;
    if (form.services.length === 0) return false;
    return true;
  };

  return (
    <Container>
      <Row>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
        <Col>
          <Card className="login-card">
            <Card.Body>
              <Card.Title className="text-center">
                {t("auth.signUpDetails.title")} Hugo,
              </Card.Title>
              <span className="d-block mt-5 lead text-center text-muted pl-5 pr-5">
                {t("auth.signUpDetails.authorDetails.title")}
              </span>
              <form className="p-2 p-t-3 p-md-3 p-xl-5" onSubmit={handleSubmit}>
                <span className="d-block lead text-muted mb-3">
                  {t("auth.signUpDetails.authorDetails.body")}
                </span>
                <Form.Group
                  className="mt-5 position-relative"
                  controlId="services"
                >
                  <TagWrapper>
                    {form.services.map((service, i) => {
                      return (
                        <Tag key={`service-tag-${i}`}>
                          <small>{service}</small>
                          <TagDeleteButton
                            type="button"
                            onClick={() => handleTagDeleteClick(i)}
                          >
                            <i className="fas fa-times"></i>
                          </TagDeleteButton>
                        </Tag>
                      );
                    })}
                  </TagWrapper>
                  <Form.Label className="sr-only">
                    {t("auth.signUpDetails.services")}
                  </Form.Label>
                  <Form.Control
                    name="services"
                    type="input"
                    placeholder={
                      form.services.length <= 1
                        ? t(
                            "auth.signUpDetails.authorDetails.servicesPlaceholder"
                          )
                        : t(
                            "auth.signUpDetails.authorDetails.servicesPlaceholderComplete"
                          )
                    }
                    disabled={form.services.length > 1}
                    onBlur={handleServicesBlur}
                    onChange={handleServicesChange}
                    value={form.currentService}
                  />
                </Form.Group>
                <Form.Group className="mt-5" controlId="location">
                  <Form.Label className="sr-only">
                    {t("auth.signUpDetails.location")}
                  </Form.Label>
                  <Form.Control
                    name="location"
                    type="input"
                    placeholder={t("auth.signUpDetails.location")}
                    onChange={handleChange}
                    value={form.location}
                  />
                </Form.Group>
                <Button
                  block
                  type="submit"
                  disabled={!isFormValid()}
                  className="mt-5"
                >
                  {t("auth.signUpDetails.next")}
                </Button>
                <Button
                  onClick={handleBackClick}
                  block
                  variant="link"
                  className="text-muted mt-0 back-button"
                >
                  <small>{t("auth.signUpDetails.back")}</small>
                </Button>
              </form>
            </Card.Body>
          </Card>
        </Col>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
      </Row>
    </Container>
  );
}

export default SignUpAuthorDetails;
