import { Button, Card, Col, Container, Form, Row } from "react-bootstrap";
import React, { useState } from "react";

import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";

function SignIn() {
  const DEFAULT_USER = {
    email: "",
    password: ""
  };

  const [user, setUser] = useState(DEFAULT_USER);
  const { t } = useTranslation();

  const handleSubmit = e => {
    e.preventDefault();
    // Logging user here for demo purposes. Use useEffect to post to API.
    console.log(user);
  };

  const handleChange = e => {
    setUser({
      ...user,
      [e.target.name]: e.target.value || ""
    });
  };

  const isFormValid = () => {
    if (!user.email || !user.email.length) return false;
    if (!user.email.match(/^\S+@\S+$/)) return false;
    if (!user.password || !user.password.length) return false;
    return true;
  };

  return (
    <Container>
      <Row>
        <Col md={6} xl={5}>
          <Card className="login-card">
            <Card.Body>
              <Card.Title className="text-center">
                {t("auth.signIn.title")}
              </Card.Title>
              <form className="text-center" onSubmit={handleSubmit}>
                <Form.Group controlId="email">
                  <Form.Label className="sr-only">
                    {t("auth.signIn.email")}
                  </Form.Label>
                  <Form.StlyedControl
                    name="email"
                    type="input"
                    placeholder="Email"
                    onChange={handleChange}
                    value={user.email}
                  />
                </Form.Group>
                <Form.Group controlId="password">
                  <Form.Label className="sr-only">
                    {t("auth.signIn.password")}
                  </Form.Label>
                  <Form.StlyedControl
                    name="password"
                    type="password"
                    placeholder="Password"
                    onChange={handleChange}
                    value={user.password}
                  />
                </Form.Group>
                <span className="d-block mt--3 mb-3 text-muted text-lowercase">
                  <small>{t("auth.forms.or")}</small>
                </span>
                <Button block type="submit">
                  <i className="fab fa-google d-inline-block pr-3"></i>{" "}
                  {t("auth.forms.googleButton")}
                </Button>
                <Button
                  block
                  type="submit"
                  disabled={!isFormValid()}
                  className="mt-5"
                >
                  {t("auth.forms.signIn")}
                </Button>
                <Link to="/forgot-password" className="d-block mt-2 text-muted">
                  <small>{t("auth.forms.forgotPassword")}</small>
                </Link>
              </form>
            </Card.Body>
          </Card>
        </Col>
        <Col className="d-none d-xl-block mt-5" xl={1}></Col>
        <Col md={6} xl={6}>
          <h1 className="mt-5 mb-3">{t("auth.signIn.signUpTitle")}</h1>
          <p>{t("auth.signIn.signUpBody")}</p>
          <Button className="mt-3" as={Link} to="/sign-up" variant="dark">
            {t("auth.signIn.signUpButton")}
          </Button>
        </Col>
      </Row>
    </Container>
  );
}

export default SignIn;
