import { BREAK_POINT_SMALL_SCREEN } from "./constants.js";
import { Card } from "react-bootstrap";
import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  display: grid;
  grid-template-columns: auto 1fr auto;

  @media (max-width: ${BREAK_POINT_SMALL_SCREEN}) {
    grid-template-columns: auto 1fr;
  }
`;
const Avatar = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 100%;

  @media (max-width: ${BREAK_POINT_SMALL_SCREEN}) {
    width: 30px;
    height: 30px;
  }
`;
const Details = styled.div`
  align-self: center;
  padding-left: 1rem;
`;
const Name = styled.span`
  line-height: 1;
`;
const Amount = styled.span`
  font-size: 1.25rem;
  align-self: center;
  display: block;

  &.text-green {
    color: #5eb948;
  }

  @media (max-width: ${BREAK_POINT_SMALL_SCREEN}) {
    font-size: 1rem;
    padding-left: 1rem;
    grid-column-start: 2;
    grid-column-end: 3;
  }
`;

function Transaction({ userAvatar, userName, date, amount, sent }) {
  return (
    <Card className="p-3 mb-3">
      <Wrapper>
        <Avatar src={userAvatar} />
        <Details>
          <Name className="d-block font-weight-bold">{userName}</Name>
          <span className="d-block text-muted">
            <small>{date.toDateString()}</small>
          </span>
        </Details>
        <Amount className={`font-weight-bold${sent ? " text-green" : ""}`}>
          {sent ? "+" : ""}
          {amount}
        </Amount>
      </Wrapper>
    </Card>
  );
}

export default Transaction;
