import { Button, Card, Col, Container, Form, Row } from "react-bootstrap";
import React, { useEffect, useState } from "react";

import { Link } from "react-router-dom";

const DEFAULT_USER = {
  firstName: "",
  lastName: "",
  email: "",
  password: "",
  agreeToTerms: false
};

function SignUp() {
  const [user, setUser] = useState(DEFAULT_USER);
  useEffect(() => {
    console.log(user);
  }, [user]);

  const handleSubmit = e => {
    e.preventDefault();
    console.log(user);
  };

  const handleChange = e => {
    setUser({
      ...user,
      [e.target.name]: e.target.value || ""
    });
  };

  const handleToggleChange = e => {
    setUser({
      ...user,
      [e.target.name]: e.target.checked
    });
  };

  const isFormValid = () => {
    if (!user.firstName || !user.firstName.length) return false;
    if (!user.lastName || !user.lastName.length) return false;
    if (!user.email || !user.email.length) return false;
    // FIXME: Add super simple email validation 
    if (!user.password || !user.password.length) return false;
    if (user.agreeToTerms === false) return false;
    return true;
  }

  return (
    <Container>
      <Row>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
        <Col>
          <Card className="login-card">
            <Card.Body>
              <Card.Title className="text-center">Join Rise</Card.Title>
              <form
                className="text-center p-2 p-t-3 p-md-3 p-xl-5"
                onSubmit={handleSubmit}
              >
                <Form.Group controlId="firstName">
                  <Form.Label className="sr-only">First Name</Form.Label>
                  <Form.Control
                    name="firstName"
                    type="input"
                    placeholder="First Name"
                    onChange={handleChange}
                    value={user.firstName}
                  />
                </Form.Group>
                <Form.Group controlId="lastName">
                  <Form.Label className="sr-only">Last Name</Form.Label>
                  <Form.Control
                    name="lastName"
                    type="input"
                    placeholder="Last Name"
                    onChange={handleChange}
                    value={user.lastName}
                  />
                </Form.Group>
                <Form.Group controlId="email">
                  <Form.Label className="sr-only">Email</Form.Label>
                  <Form.Control
                    name="email"
                    type="input"
                    placeholder="Email"
                    onChange={handleChange}
                    value={user.email}
                  />
                </Form.Group>
                <Form.Group controlId="password">
                  <Form.Label className="sr-only">Password</Form.Label>
                  <Form.Control
                    name="password"
                    type="password"
                    placeholder="Password"
                    onChange={handleChange}
                    value={user.password}
                  />
                </Form.Group>
                <Form.Group controlId="agreeToTerms">
                  <Form.Label className="sr-only">Agree to Terms?</Form.Label>
                  <Form.Check
                    name="agreeToTerms"
                    type="switch"
                    id="custom-switch"
                    label={
                      <span>
                        By joining Rise you agree to our{" "}
                        <a
                          target="blank"
                          href="https://www.riseworks.io/terms/"
                        >
                          Terms & Conditions
                        </a>{" "}
                        and{" "}
                        <a
                          target="blank"
                          href="https://www.riseworks.io/privacy/"
                        >
                          Privacy Policy.
                        </a>
                      </span>
                    }
                    onChange={handleToggleChange}
                    value={user.agreeToTerms}
                  />
                </Form.Group>
                <Button block type="submit" disabled={!isFormValid()}>
                  Sign Up
                </Button>
                <Link to="/log-in">Already with us? Log in.</Link>
              </form>
            </Card.Body>
          </Card>
        </Col>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
      </Row>
    </Container>
  );
}

export default SignUp;
