import { BREAK_POINT_LARGE_SCREEN } from "Projects/constants";
import Chat from "./Chat";
import { Container } from "react-bootstrap";
import Controls from "./Controls";
import MyProjects from "./MyProjects";
import React from "react";
import projects from "./data";
import styled from "styled-components";
import { useParams } from "react-router-dom";

const Wrapper = styled(Container)`
  display: grid;
  grid-template-columns: 240px 1fr 300px;
  grid-column-gap: 1rem;

  @media (max-width: ${BREAK_POINT_LARGE_SCREEN}) {
    grid-template-columns: 100%;
    grid-row-gap: 1rem;
  }
`;

function Projects() {
  const { currentProjectID } = useParams();
  const currentProject =
    currentProjectID && projects.find(p => p.id === currentProjectID);
  const currentProjectPartner = currentProject ? currentProject.partner : {};
  const currentProjectMessages = currentProject ? currentProject.messages : [];

  return (
    <Wrapper>
      <MyProjects projects={projects} currentProjectID={currentProjectID} />
      <Chat
        currentProjectID={currentProjectID}
        partner={currentProjectPartner}
        messages={currentProjectMessages}
      />
      {currentProject ? (
        <Controls
          projectName={currentProject.title}
          tasks={currentProject.tasks}
        />
      ) : null}
    </Wrapper>
  );
}

export default Projects;
