import { Button, Col, Row } from "react-bootstrap";
import { CURRENCY_DAI, CURRENCY_ETH } from "./Wallet/constants";

import { Link } from "react-router-dom";
import React from "react";
import Wallet from "./Wallet";
import { useTranslation } from "react-i18next";

// NOTE: This will ultimately come from an API, but it is here to show that
// all components are created from JSON data.
const walletData = [
  {
    title: "Work Wallet",
    walletID: "7Am9gVZynQvjecVUD6GuCyiR24uoWi2MM9",
    amount: 22.789,
    currency: CURRENCY_DAI,
    transactions: [
      {
        id: 123,
        userAvatar: "https://i.pravatar.cc/100",
        userName: "Craig Siphron",
        date: new Date(),
        amount: "3.1213 ETH",
        sent: true // NOTE this indicates whether this user sent or received the amount. This is probably the wrong term.
      },
      {
        id: 456,
        userAvatar: "https://i.pravatar.cc/101",
        userName: "Justin Smith",
        date: new Date(),
        amount: "21.3349 ETH",
        sent: false
      }
    ]
  },
  {
    title: "Home Wallet",
    walletID: "1Fm9gVZynQvjecVUD6GuCyiR24uoWi2JK6",
    amount: 100.1234,
    currency: CURRENCY_ETH,
    transactions: [
      {
        id: 789,
        userAvatar: "https://i.pravatar.cc/102",
        userName: "Andrew Maurer",
        date: new Date(),
        amount: "7.9445 ETH",
        sent: true
      },
      {
        id: 1011,
        userAvatar: "https://i.pravatar.cc/103",
        userName: "Hugo Finklestein",
        date: new Date(),
        amount: "250 DAI",
        sent: false
      },
      {
        id: 1213,
        userAvatar: "https://i.pravatar.cc/104",
        userName: "Charles Brown",
        date: new Date(),
        amount: "7.6501 ETH",
        sent: false
      }
    ]
  }
];

function MyWallets() {
  const { t } = useTranslation();

  return (
    <>
      {walletData.map(wallet => {
        return (
          <Wallet
            key={`wallet-${wallet.walletID}`}
            title={wallet.title}
            walletID={wallet.walletID}
            amount={wallet.amount}
            currency={wallet.currency}
            transactions={wallet.transactions}
          />
        );
      })}
      <Row className="mt-5 mb-5">
        <Col lg={3}></Col>
        <Col lg={6}>
          <Button block as={Link} to="/wallet/create" variant="primary">
            {t("wallet.newWallet")}
          </Button>
        </Col>
        <Col lg={3}></Col>
      </Row>
    </>
  );
}

export default MyWallets;
