import React, { useEffect, useState } from "react";

import StepWalletDownload from "./StepWalletDownload";
import StepWalletPassword from "./StepWalletPassword";
import StepWalletType from "./StepWalletType";

let DEFAULT_WALLET = {
  type: null,
  password: null
};

function CreateWallet() {
  const [step, setStep] = useState(0);
  const [wallet, setWallet] = useState(DEFAULT_WALLET);
  const [isFetching, setIsFetching] = useState(false);
  useEffect(() => {
    if (step === 2) {
      // This is where we would submit the wallet to the API.
      setIsFetching(true);
      setTimeout(()=>{
        setIsFetching(false);
      }, 2000);
    }
  }, [step, wallet]);


  const handleSubmit = (updates) => {
    setWallet({
      ...wallet,
      ...updates
    });
    setStep(step + 1);
  };


  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <StepWalletPassword onSubmit={handleSubmit} />
        );
      case 2:
        return <StepWalletDownload isFetching={isFetching} />;
      default:
        return (
          <StepWalletType onSubmit={handleSubmit} />
        );
    }
  };

  return <div>{renderStep()}</div>;
}

export default CreateWallet;
