import { Button, Card, Col, Container, Row } from "react-bootstrap";

import React from "react";

function StepWalletType({onSubmit}) {
  const handleSubmit = updates => {
    onSubmit(updates);
  }

  return (
    <Card>
      <Card.Body>
        <Card.Title>Generate New Wallet</Card.Title>
        <Container className="pl-0 pr-0 mt-5 mb-5">
          <Row className="align-items-center">
            <Col lg={2} className="d-none d-lg-flex"></Col>
            <Col md={5} lg={3} className="text-md-right">
              <Button
                block
                onClick={() => {
                  handleSubmit({ type: "rise" });
                }}
              >
                Rise
              </Button>
            </Col>
            <Col md={2} className="text-center">
              <span className="text-uppercase text-muted">
                <small>Or</small>
              </span>
            </Col>
            <Col md={5} lg={3} className="text-md-left">
              <Button
                block
                onClick={() => {
                  handleSubmit({ type: "metaHask" });
                }}
              >
                MetaHask
              </Button>
            </Col>
            <Col lg={2} className="d-none d-lg-flex"></Col>
          </Row>
        </Container>
        <span className="d-block text-muted">
          <small>- Your wallet cannot be recovered.</small>
        </span>
        <span className="d-block text-muted">
          <small>
            - You can download a backup file at the final step. Make sure to
            save it somewhere safe.
          </small>
        </span>
      </Card.Body>
    </Card>
  );
}

export default StepWalletType;
