import { Button, Card, Col, Container, Form, Row } from "react-bootstrap";
import { Link, useHistory } from "react-router-dom";
import React, { useState } from "react";

import { useTranslation } from "react-i18next";

const DEFAULT_USER = {
  firstName: "",
  lastName: "",
  email: "",
  password: "",
  agreeToTerms: false
};

function SignUp() {
  const [user, setUser] = useState(DEFAULT_USER);
  const { t } = useTranslation();
  let history = useHistory();

  const handleSubmit = e => {
    e.preventDefault();
    // Logging user here for demo purposes. Use useEffect to post to API.
    console.log(user);
    history.push("/sign-up/about-you");
  };

  const handleChange = e => {
    setUser({
      ...user,
      [e.target.name]: e.target.value || ""
    });
  };

  const handleToggleChange = e => {
    setUser({
      ...user,
      [e.target.name]: e.target.checked
    });
  };

  const isFormValid = () => {
    if (!user.firstName || !user.firstName.length) return false;
    if (!user.lastName || !user.lastName.length) return false;
    if (!user.email || !user.email.length) return false;
    if (!user.email.match(/^\S+@\S+$/)) return false;
    if (!user.password || !user.password.length) return false;
    if (user.agreeToTerms === false) return false;
    return true;
  };

  return (
    <Container>
      <Row>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
        <Col>
          <Card className="login-card">
            <Card.Body>
              <Card.Title className="text-center">
                {t("auth.signUp.title")}
              </Card.Title>
              <form
                className="text-center p-2 p-t-3 p-md-3 p-xl-5"
                onSubmit={handleSubmit}
              >
                <Form.Group controlId="firstName">
                  <Form.Label className="sr-only">
                    {t("auth.forms.firstName")}
                  </Form.Label>
                  <Form.StlyedControl
                    name="firstName"
                    type="input"
                    placeholder={t("auth.forms.firstName")}
                    onChange={handleChange}
                    value={user.firstName}
                  />
                </Form.Group>
                <Form.Group controlId="lastName">
                  <Form.Label className="sr-only">
                    {t("auth.forms.lastName")}
                  </Form.Label>
                  <Form.StlyedControl
                    name="lastName"
                    type="input"
                    placeholder={t("auth.forms.lastName")}
                    onChange={handleChange}
                    value={user.lastName}
                  />
                </Form.Group>
                <Form.Group controlId="email">
                  <Form.Label className="sr-only">
                    {t("auth.forms.email")}
                  </Form.Label>
                  <Form.StlyedControl
                    name="email"
                    type="input"
                    placeholder={t("auth.forms.email")}
                    onChange={handleChange}
                    value={user.email}
                  />
                </Form.Group>
                <Form.Group controlId="password">
                  <Form.Label className="sr-only">
                    {t("auth.forms.password")}
                  </Form.Label>
                  <Form.StlyedControl
                    name="password"
                    type="password"
                    placeholder={t("auth.forms.password")}
                    onChange={handleChange}
                    value={user.password}
                  />
                </Form.Group>
                <span className="d-block mt--3 mb-3 text-muted text-lowercase">
                  <small>{t("auth.forms.or")}</small>
                </span>
                <Button block type="submit">
                  <i className="fab fa-google d-inline-block pr-3"></i>{" "}
                  {t("auth.forms.googleButton")}
                </Button>
                <Form.Group className="mt-5" controlId="agreeToTerms">
                  <Form.Label className="sr-only">
                    {t("auth.forms.agreeToTerms")}
                  </Form.Label>
                  <Form.Check
                    name="agreeToTerms"
                    type="switch"
                    label={
                      <span className="text-left d-inline-block pl-3">
                        <small>
                          {t("auth.forms.agreeToTerms1")}{" "}
                          <a
                            target="blank"
                            href="https://www.riseworks.io/terms/"
                            className="text-muted"
                          >
                            {t("footer.terms")}
                          </a>{" "}
                          {t("auth.forms.agreeToTerms2")}{" "}
                          <a
                            target="blank"
                            href="https://www.riseworks.io/privacy/"
                            className="text-muted"
                          >
                            {t("footer.privacy")}.
                          </a>
                        </small>
                      </span>
                    }
                    onChange={handleToggleChange}
                    value={user.agreeToTerms}
                  />
                </Form.Group>
                <Button
                  block
                  type="submit"
                  disabled={!isFormValid()}
                  className="mt-5"
                >
                  {t("auth.forms.signUp")}
                </Button>
                <Link to="/sign-in" className="text-muted mt-2 d-block">
                  <small>{t("auth.forms.alreadyWithUs")}</small>
                </Link>
              </form>
            </Card.Body>
          </Card>
        </Col>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
      </Row>
    </Container>
  );
}

export default SignUp;
