import { Button, ButtonGroup, Card, Form } from "react-bootstrap";

import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
  @media (max-width: 400px) {}
  flex-direction: column;
`;

const FormGroup = styled(Form.Group)`
  flex-grow: 1;
  margin-bottom: 0;
  margin-left: 1rem;
  margin-right: 1rem;

  .form-control {
    box-shadow: none;
  }
`;

function SendForm() {
  return (
    <Card className="mt-5 pl-3 pr-3">
      <Wrapper>
        <ButtonGroup aria-label="Send options">
          <Button variant="light">DAI</Button>
          <Button variant="light">ETH</Button>
        </ButtonGroup>
        <FormGroup controlId="walletID">
          <Form.Label className="sr-only">Wallet ID</Form.Label>
          <Form.Control type="text" placeholder="Wallet ID" />
        </FormGroup>
        <Button variant="light">
          Send <i className="fas fa-arrow-circle-right"></i>
        </Button>
      </Wrapper>
    </Card>
  );
}

export default SendForm;
