import {
  ButtonGroup as BootstrapButtonGroup,
  Button,
  Card,
  Col,
  Form,
  Modal
} from "react-bootstrap";
import { CURRENCY_DAI, CURRENCY_ETH } from "./constants.js";
import React, { useState } from "react";

import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
  @media (max-width: 400px) {
    flex-direction: column;
  }
`;

const ButtonGroup = styled(BootstrapButtonGroup)`
  padding-left: 0.5rem;
  padding-right: 1rem;
  margin-right: 1rem;
  border-right: 1px solid rgba(0, 0, 0, 0.1);
`;



const SendAmountFormRow = styled(Form.Row)`
  & .form-group {
    padding-right: 0;
  }

  & .form-group + .form-group {
    padding-left: 0;
  }

  & .btn-group {
    height: calc(100% - 2px);
    transform: translateY(1px);
    padding-left: 0;

    & .btn:first-child {
      border-top-left-radius: 0;
      border-bottom-left-radius: 0;
    }
  }

  & .form-control {
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
  }
`;

const DEFAULT_SEND_FORM = {
  walletID: "",
  amount: "",
  currency: CURRENCY_DAI
};

function SendForm({ currency }) {
  const [showConvertModal, setShowConvertModal] = useState(false);
  const [showSendModal, setSendConvertModal] = useState(false);
  const [sendForm, setSendForm] = useState(DEFAULT_SEND_FORM);

  const handleConvertClick = () => {
    setShowConvertModal(true);
  };

  const handleCloseConvertModal = () => {
    setShowConvertModal(false);
  };

  const handleSendClick = () => {
    setSendConvertModal(true);
  };

  const handleCloseSendModal = () => {
    setSendForm(DEFAULT_SEND_FORM);
    setSendConvertModal(false);
  };

  const handleSendFormChange = e => {
    setSendForm({
      ...sendForm,
      [e.target.name]: e.target.value
    });
  };

  return (
    <>
      <Card className="mt-5 pl-3 pr-3">
        <Wrapper>

          <ButtonGroup aria-label="Send options">
            <Button
              disabled={currency === CURRENCY_DAI}
              onClick={handleConvertClick}
              variant={currency === CURRENCY_DAI ? "primary" : "light"}
            >
              DAI
            </Button>
            <Button
              disabled={currency === CURRENCY_ETH}
              onClick={handleConvertClick}
              variant={currency === CURRENCY_ETH ? "primary" : "light"}
            >
              ETH
            </Button>
          </ButtonGroup>
          <Button block onClick={handleSendClick} variant="light">
            Send <i className="fas fa-arrow-circle-right"></i>
          </Button>
        </Wrapper>
      </Card>

      <Modal show={showConvertModal} onHide={handleCloseConvertModal}>
        <Modal.Header closeButton>
          <Modal.Title>Convert Wallet</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to convert your wallet to{" "}
          {currency === CURRENCY_ETH ? CURRENCY_DAI : CURRENCY_ETH}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="link" onClick={handleCloseConvertModal}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleCloseConvertModal}>
            Convert
          </Button>
        </Modal.Footer>
      </Modal>

      <Modal show={showSendModal} onHide={handleCloseSendModal}>
        <Modal.Header closeButton>
          <Modal.Title>Send Money</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Send money to another Rise user.</p>
          <Form.Group controlId="walletID">
            <Form.Label className="sr-only">Wallet ID</Form.Label>
            <Form.Control
              name="walletID"
              type="text"
              placeholder="Wallet ID"
              value={sendForm.walletID}
              onChange={handleSendFormChange}
            />
          </Form.Group>
          <SendAmountFormRow>
            <Form.Group as={Col} controlId="formGridCity">
              <Form.Label className="sr-only">Amount</Form.Label>
              <Form.Control placeholder="Amount" />
            </Form.Group>

            <Form.Group as={Col} controlId="formGridCity">
              <BootstrapButtonGroup as={Col} aria-label="Send options">
                <Button
                  disabled={currency === CURRENCY_DAI}
                  onClick={handleConvertClick}
                  variant={currency === CURRENCY_DAI ? "primary" : "light"}
                >
                  DAI
                </Button>
                <Button
                  disabled={currency === CURRENCY_ETH}
                  onClick={handleConvertClick}
                  variant={currency === CURRENCY_ETH ? "primary" : "light"}
                >
                  ETH
                </Button>
              </BootstrapButtonGroup>
            </Form.Group>
          </SendAmountFormRow>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="link" onClick={handleCloseSendModal}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleCloseSendModal}>
            Send
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default SendForm;
