import { Button, Card, Col, Container, Form, Row } from "react-bootstrap";
import React, { useState } from "react";
import { useTranslation } from "react-i18next";

function SignUpRole({ defaultValues=[], onSubmit }) {
  const DEFAULT_FORM = {
    findWork: defaultValues.includes("findWork"),
    hireTalent: defaultValues.includes("hireTalent")
  };

  const [form, setForm] = useState(DEFAULT_FORM);
  const { t } = useTranslation();
  const handleSubmit = e => {
    e.preventDefault();
    // Convert form values to an array of roles based on which items are toggled.
    // i.e. {findWork: true, hireTalent: false} becomes ["findWork"]
    // i.e. {findWork: true, hireTalent: true} becomes ["findWork", "hireTalent"]
    onSubmit({
      roles: Object.keys(form)
        .map(key => {
          const val = form[key];
          if (val) {
            return key;
          }
          return null;
        })
        .filter(x => x)
    });
  };

  const handleToggleChange = e => {
    setForm({
      ...form,
      [e.target.name]: e.target.checked
    });
  };

  const isFormValid = () => {
    if (form.findWork || form.hireTalent) return true;
    return false;
  };

  return (
    <Container>
      <Row>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
        <Col>
          <Card className="login-card">
            <Card.Body>
              <Card.Title className="text-center">{t("auth.signUpDetails.title")} Hugo,</Card.Title>
              <span className="d-block mt-5 lead text-center text-muted pl-5 pr-5">
                {t("auth.signUpDetails.signUpRole.title")}
              </span>
              <form
                className="text-center p-2 p-t-3 p-md-3 p-xl-5"
                onSubmit={handleSubmit}
              >
                <Form.Group controlId="findWork">
                  <Form.Label className="sr-only">{t("auth.signUpDetails.signUpRole.roleWork")}</Form.Label>
                  <Form.Check className="reverse d-flex" type="switch">
                    <Form.Check.Input
                      name="findWork"
                      type="checkbox"
                      checked={form.findWork}
                      onChange={handleToggleChange}
                    />
                    <Form.Check.Label className="flex-grow-1">
                      <span className="text-left d-block lead text-muted">
                      {t("auth.signUpDetails.signUpRole.roleWork")}
                      </span>
                    </Form.Check.Label>
                  </Form.Check>
                </Form.Group>
                <Form.Group className="mt-5" controlId="hireTalent">
  <Form.Label className="sr-only">{}</Form.Label>
                  <Form.Check className="reverse d-flex" type="switch">
                    <Form.Check.Input
                      name="hireTalent"
                      type="checkbox"
                      checked={form.hireTalent}
                      onChange={handleToggleChange}
                    />
                    <Form.Check.Label className="flex-grow-1">
                      <span className="text-left d-block lead text-muted">
                        Hire Talent
                      </span>
                    </Form.Check.Label>
                  </Form.Check>
                </Form.Group>
                <Button
                  block
                  type="submit"
                  disabled={!isFormValid()}
                  className="mt-5"
                >
                  Next
                </Button>
              </form>
            </Card.Body>
          </Card>
        </Col>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
      </Row>
    </Container>
  );
}

export default SignUpRole;
