import { Accordion, Container } from "react-bootstrap";
import React, { useState } from "react";

import Card from "../Card";
import Information from "./Information";
import QuickAccess from "./QuickAccess";
import { Route } from "react-router-dom";
import Tasks from "./Tasks";
import { formatDistance } from "date-fns";
import styled from "styled-components";

const HeaderContent = styled.div`
  .small {
    font-size: 0.875rem;
  }
`;
const dueDate = new Date() - 100000000;

const TYPE_TASKS = "tasks";
const TYPE_INFORMATION = "information";
const TYPE_QUICK_ACCESS = "quickAccess";

function Controls({ projectName, tasks }) {
  const [eventKey, setEventKey] = useState(TYPE_TASKS);
  console.log(dueDate);
  return (
    <Card>
      <Card.Body>
        <Card.Title>
          <HeaderContent className="d-flex align-items-center">
            <span className="d-block flex-grow-1">{projectName}</span>
            <span className="d-block font-weight-light">
              <small>
                {formatDistance(new Date(), dueDate, {
                  addSuffix: false
                })}{" "}
                left
              </small>
            </span>
          </HeaderContent>
        </Card.Title>
        <Accordion
          className="mt-4"
          onSelect={setEventKey}
          defaultActiveKey={TYPE_TASKS}
        >
          <Tasks
            type={TYPE_TASKS}
            isExpanded={eventKey === TYPE_TASKS}
            tasks={tasks}
          />
          <Information
            type={TYPE_INFORMATION}
            isExpanded={eventKey === TYPE_INFORMATION}
          />
          <QuickAccess
            type={TYPE_QUICK_ACCESS}
            isExpanded={eventKey === TYPE_QUICK_ACCESS}
          />
        </Accordion>
      </Card.Body>
    </Card>
  );
}

export default Controls;
