import React, { useState } from "react";
import { Button, Card, Modal } from "react-bootstrap";
import Service from "./Service";

function Services({ services }) {
  const [showAddModal, setShowAddModal] = useState(false);

  const handleAddServiceClick = () => {
    setShowAddModal(true);
  };

  const handleCloseAddModal = () => {
    setShowAddModal(false);
  };
  return (
    <>
      <Card>
        <Card.Body>
          <Card.Title>Services</Card.Title>
          {services.map(service => {
            console.log(service);
            return <Service key={`service-${service.id}`} service={service} />;
          })}
          <Button onClick={handleAddServiceClick}>Add Service</Button>
        </Card.Body>
      </Card>
      <Modal show={showAddModal} onHide={handleCloseAddModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add Service</Modal.Title>
        </Modal.Header>
        <Modal.Body>form to add new service goes here</Modal.Body>
        <Modal.Footer>
          <Button variant="link" onClick={handleCloseAddModal}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleCloseAddModal}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Services;
