import { Button, Card, Col, Container, Form, Row } from "react-bootstrap";
import React, { useState } from "react";

import ServiceImage from "./ServiceImage";
import Spinner from "SharedComponents/Spinner";
import services from "constants/services";
import styled from "styled-components";

import {  } from "react-i18next";

const Grid = styled.div`
  display: grid;
  grid-template-columns: 50% 50%;
  grid-column-gap: 10px;
  grid-row-gap: 2px;
`;

function SignUpClientDetails({
  defaultValues = [],
  isFetching,
  onBackClick,
  onSubmit
}) {
  const DEFAULT_FORM = {
    services: services.map(service => ({
      ...service,
      checked: defaultValues.includes(service.key)
    }))
  };

  const [form, setForm] = useState(DEFAULT_FORM);

  const getSubmitPayload = () => {
    return {
      preferredServices: form.services
        .map(service => {
          if (service.checked) {
            return service.key;
          }
          return null;
        })
        .filter(x => x)
    };
  };

  const handleSubmit = e => {
    e.preventDefault();
    onSubmit(getSubmitPayload());
  };

  const handleBackClick = () => {
    onBackClick(getSubmitPayload());
  };

  const handleToggleChange = e => {
    const { name, checked } = e.target;
    setForm({
      ...form,
      services: form.services.map((service, i) => {
        if (service.key === name) {
          return { ...service, checked };
        }
        return service;
      })
    });
  };

  const handleButtonClick = e => {
    const { name } = e.target;
    setForm({
      ...form,
      services: form.services.map((service, i) => {
        if (service.key === name) {
          return {
            ...service,
            checked: !form.services.find(s => s.key === name).checked
          };
        }
        return service;
      })
    });
  };

  const isFormValid = () => {
    if (
      !form.services.reduce((acc, service) => {
        return acc || service.checked;
      }, false)
    ) {
      return false;
    }
    return true;
  };

  return (
    <Container>
      <Row>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
        <Col>
          <Card className="login-card">
            <Card.Body>
              <Card.Title className="text-center">Hello Hugo,</Card.Title>
              <span className="d-block mt-5 lead text-center text-muted pl-5 pr-5">
                Who do you want to connect to?
              </span>
              <form className="p-2 p-t-3 p-md-3 p-xl-5" onSubmit={handleSubmit}>
                <Grid>
                  {form.services.map(service => {
                    return (
                      <Form.Group
                        className="mb-0"
                        key={`service-${service.key}`}
                        controlId={`service-${service.key}`}
                      >
                        <Form.Label className="sr-only">
                          {service.name}
                        </Form.Label>
                        <Form.Check
                          className="p-0"
                          name={service.key}
                          type="checkbox"
                        >
                          <Form.Check.Label className="w-100">
                            <ServiceImage
                              image={service.image}
                              keyName={service.key}
                              name={service.name}
                              checked={service.checked}
                              count={service.count}
                              onButtonClick={handleButtonClick}
                            />
                          </Form.Check.Label>
                          <Form.Check.Input
                            className="invisible"
                            name={service.key}
                            type="checkbox"
                            onChange={handleToggleChange}
                            checked={service.checked}
                          />
                        </Form.Check>
                      </Form.Group>
                    );
                  })}
                </Grid>
                <Button
                  block
                  type="submit"
                  disabled={!isFormValid()}
                  className="mt-5"
                >
                  Next
                </Button>
                <Button
                  onClick={handleBackClick}
                  block
                  variant="link"
                  className="text-muted mt-0 back-button"
                >
                  <small>Back.</small>
                </Button>
              </form>
            </Card.Body>
            {isFetching ? <Spinner /> : null}
          </Card>
        </Col>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
      </Row>
    </Container>
  );
}

export default SignUpClientDetails;
