import {
  TASK_STATUS_COMPLETE,
  TASK_STATUS_INCOMPLETE,
  TASK_STATUS_IN_PROGRESS
} from "./constants";

import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
`;
const Status = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: ${({ status }) => {
    if (status === TASK_STATUS_COMPLETE) {
      return "blue";
    }
    if (status === TASK_STATUS_INCOMPLETE) {
      return "red";
    }
    if (status === TASK_STATUS_IN_PROGRESS) {
      return "green";
    }
    return "black";
  }};
`;

function Task({ title, status }) {
  return (
    <Wrapper>
      <Status status={status} />
      {title}
    </Wrapper>
  );
}

export default Task;
