import { Button, Card, Col, Container, Form, Row, } from "react-bootstrap";
import React, { useEffect, useState } from "react";

import { Link } from "react-router-dom";

const DEFAULT_USER = {
  email: "",
  password: "",
};

function SignIn() {
  const [user, setUser] = useState(DEFAULT_USER);

  const handleSubmit = e => {
    e.preventDefault();
    console.log(user);
  };

  const handleChange = e => {
    setUser({
      ...user,
      [e.target.name]: e.target.value || ""
    });
  };

  const isFormValid = () => {
    if (!user.email || !user.email.length) return false;
    // FIXME: Add super simple email validation
    if (!user.password || !user.password.length) return false;
    return true;
  };

  return (
    <Container>
      <Row>
        <Col md={6} lg={4}>
          <Card className="login-card">
            <Card.Body>
              <Card.Title className="text-center">Join Rise</Card.Title>
              <form className="text-center" onSubmit={handleSubmit}>
                <Form.Group controlId="email">
                  <Form.Label className="sr-only">Email</Form.Label>
                  <Form.Control
                    name="email"
                    type="input"
                    placeholder="Email"
                    onChange={handleChange}
                    value={user.email}
                  />
                </Form.Group>
                <Form.Group controlId="password">
                  <Form.Label className="sr-only">Password</Form.Label>
                  <Form.Control
                    name="password"
                    type="password"
                    placeholder="Password"
                    onChange={handleChange}
                    value={user.password}
                  />
                </Form.Group>
                <span className="d-block mt-2 mb-3">Or</span>
                <Button
                  block
                  type="submit"
                  disabled={!isFormValid()}
                  variant="light"
                >
                  <i className="fab fa-google"></i> Continue with Google
                </Button>
                <Button
                  block
                  type="submit"
                  disabled={!isFormValid()}
                  className="mt-5"
                >
                  Sign In
                </Button>
                <Link to="/forgot-password">Forgotten your password?</Link>
              </form>
            </Card.Body>
          </Card>
        </Col>
        <Col className="d-none d-lg-block" lg={2}></Col>
        <Col md={6}>
          <h1 className="mt-5">Sign Up</h1>
          <p>
            We're developing a marketplace for freelance services. This
            marketplace will leverage blockchain technology and smart contracts
            to facilitate exchanges between independent professionals and
            clients. Why not join in?
          </p>
          <Button as={Link} to="/sign-up">
            Sign Up
          </Button>
        </Col>
      </Row>
    </Container>
  );
}

export default SignIn;
