import ControlItem from "../ControlItem";
import React from "react";
import Task from "./Task";
import styled from "styled-components";

function Tasks({ isExpanded, type, tasks = [] }) {
  return (
    <ControlItem type={type} isExpanded={isExpanded} title="Tasks">
      {tasks.map(task => {
        return (
          <Task
            key={`task-${task.id}`}
            title={task.title}
            status={task.status}
          />
        );
      })}
    </ControlItem>
  );
}

export default Tasks;
