import "styles.scss";

import { Route, BrowserRouter as Router, Switch } from "react-router-dom";

import { AppContext } from "Context";
import Auth from "Auth";
import { Container } from "react-bootstrap";
import Footer from "SharedComponents/Footer";
import Nav from "SharedComponents/Nav";
import NotFound from "NotFound";
import Projects from "Projects";
import React from "react";
import Wallet from "Wallet";
import styled from "styled-components";

const PageContainer = styled.div`
  margin-top: 7rem;
  margin-bottom: 2rem;
  min-height: 75vh;
`;

const defaultAppContext = {
  user: {
    id: 99999,
    name: "Andrew Maurer",
    avatar: "https://i.pravatar.cc/101"
  }
};

function App() {
  return (
    <AppContext.Provider value={defaultAppContext}>
      <Router>
        <Nav />
        <PageContainer>
          <Switch>
            {/* This is a parent route. Typically this might be for a top level 
                path (i.e. "/account"). Components can have their own child 
                routes (i.e. "/account/wallet"). Since we only have one section
                in the demo app, we are just routing all traffic to the Demo
                component. */}
            <Route exact path="/">
              <Container>
                This is the app homepage. We will eventually remove this since
                the homepage is not part of the React app.
                <br />
              </Container>
            </Route>
            <Route path="/sign-in">
              <Auth />
            </Route>
            <Route path="/sign-up">
              <Auth />
            </Route>
            <Route path="/projects/:currentProjectID?">
              <Projects />
            </Route>
            <Route path="/wallet">
              <Wallet />
            </Route>
            <Route path="/account">
              <NotFound />
            </Route>
          </Switch>
        </PageContainer>
        <Footer />
      </Router>
    </AppContext.Provider>
  );
}

export default App;
