import { Overlay, Tooltip } from "react-bootstrap";
import React, { useRef, useState } from "react";

import styled from "styled-components";

const Wrapper = styled.div`
  align-self: end;
  color: #757575;
  cursor: pointer;
  font-size: 0.8rem;
  font-weight: 400;
  line-height: 1.5rem;
  padding-left: 1rem;
  transform: translateY(3px);

  @media 

  &:hover {
    color: #333;
  }
`;

const HiddenInput = styled.input`
  left: -100px;
  opacity: 0;
  position: fixed;
  top: -100px;
`;

let timeoutId;

function WalletID({ walletID }) {
  const [showCopiedTooltip, setShowCopiedTooltip] = useState(false);
  const inputRef = useRef(null);
  const tooltipTargetRef = useRef(null);

  const handleClick = () => {
    window.clearTimeout(timeoutId);
    const input = inputRef.current;
    input.select();
    document.execCommand("copy");
    setShowCopiedTooltip(true);
    timeoutId = window.setTimeout(() => {
      setShowCopiedTooltip(false);
    }, 1000);
  };

  return (
    <Wrapper className="transition-all">
      <span ref={tooltipTargetRef} onClick={handleClick}>
        {walletID} <i className="fas fa-copy"></i>
      </span>
      <Overlay
        target={tooltipTargetRef.current}
        show={showCopiedTooltip}
        placement="top"
      >
        {props => (
          <Tooltip id="overlay-example" {...props} show={null}>
            Walled ID Copied
          </Tooltip>
        )}
      </Overlay>
      <HiddenInput tabIndex={0} ref={inputRef} defaultValue={walletID} />
    </Wrapper>
  );
}

export default WalletID;
