import { Accordion, Card, Container } from "react-bootstrap";
import {
  PROJECT_STATUS_COMPLETE,
  PROJECT_STATUS_OPEN,
  PROJECT_STATUS_PENDING
} from "../constants";
import React, { useState } from "react";

import ProjectGroup from "./ProjectGroup";
import { Route } from "react-router-dom";
import styled from "styled-components";

const Wrapper = styled(Container)`
  margin-top: 14rem;
`;

function MyProjects({ currentProjectID, projects = [] }) {
  const getDefaultActiveKey = () => {
    if (!currentProjectID) return PROJECT_STATUS_OPEN;
    const currentProject = projects.find(p => p.id === currentProjectID);
    if (!currentProject) return PROJECT_STATUS_OPEN;
    return currentProject.status || PROJECT_STATUS_OPEN;
  };
  const [eventKey, setEventKey] = useState(getDefaultActiveKey());

  const openProjects = projects.filter(p => p.status === PROJECT_STATUS_OPEN);
  const completeProjects = projects.filter(
    p => p.status === PROJECT_STATUS_COMPLETE
  );
  const pendingProjects = projects.filter(
    p => p.status === PROJECT_STATUS_PENDING
  );

  return (
    <Card>
      <Card.Body>
        <Card.Title>Projects</Card.Title>
        <Accordion
          className="mt-4"
          onSelect={setEventKey}
          defaultActiveKey={getDefaultActiveKey()}
        >
          <ProjectGroup
            currentProjectID={currentProjectID}
            type={PROJECT_STATUS_OPEN}
            isExpanded={eventKey === PROJECT_STATUS_OPEN}
            projects={openProjects}
          />
          <ProjectGroup
            currentProjectID={currentProjectID}
            type={PROJECT_STATUS_COMPLETE}
            isExpanded={eventKey === PROJECT_STATUS_COMPLETE}
            projects={completeProjects}
          />
          <ProjectGroup
            currentProjectID={currentProjectID}
            type={PROJECT_STATUS_PENDING}
            isExpanded={eventKey === PROJECT_STATUS_PENDING}
            projects={pendingProjects}
          />
        </Accordion>
      </Card.Body>
    </Card>
  );
}

export default MyProjects;
