import { Button, Card, Col, Container, Form, Row } from "react-bootstrap";
import React, { useState } from "react";

import services from "constants/services";
import {  } from "react-il";
function SignUpAuthorDetails({
  defaultValues = { services: [], location: "" },
  onBackClick,
  onSubmit
}) {
  const DEFAULT_FORM = {
    services: services.map(service => ({
      ...service,
      checked: defaultValues.services.includes(service.key)
    })),
    location: defaultValues.location
  };

  const [form, setForm] = useState(DEFAULT_FORM);

  const getSubmitPayload = () => {
    return {
      location: form.location,
      services: form.services
        .map(service => {
          if (service.checked) {
            return service.key;
          }
          return null;
        })
        .filter(x => x)
    };
  };

  const handleSubmit = e => {
    e.preventDefault();
    onSubmit(getSubmitPayload());
  };

  const handleBackClick = () => {
    onBackClick(getSubmitPayload());
  };

  const handleChange = e => {
    setForm({
      ...form,
      [e.target.name]: e.target.value || ""
    });
  };

  const handleToggleChange = e => {
    const { name, checked } = e.target;
    setForm({
      ...form,
      services: form.services.map((service, i) => {
        if (service.key === name) {
          return { ...service, checked };
        }
        return service;
      })
    });
  };

  const isFormValid = () => {
    console.log(form.services)
    if (!form.location || !form.location.length) return false;
    if (!form.services.reduce((acc, service) => acc || service.checked, false)) {
      return false;
    }
    return true;
  };

  return (
    <Container>
      <Row>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
        <Col>
          <Card className="login-card">
            <Card.Body>
              <Card.Title className="text-center">Hello Hugo,</Card.Title>
              <span className="d-block mt-5 lead text-center text-muted pl-5 pr-5">
                Let us know a bit about you
              </span>
              <form className="p-2 p-t-3 p-md-3 p-xl-5" onSubmit={handleSubmit}>
                <span className="d-block lead text-muted mb-3">
                  Select your services:
                </span>
                {form.services.map(service => {
                  return (
                    <Form.Group
                      key={`service-${service.key}`}
                      controlId={`service-${service.key}`}
                    >
                      <Form.Label className="sr-only">
                        {service.name}
                      </Form.Label>
                      <Form.Check
                        name={service.key}
                        onChange={handleToggleChange}
                        type="checkbox"
                        label={service.name}
                        checked={service.checked}
                      />
                    </Form.Group>
                  );
                })}
                <Form.Group className="mt-5" controlId="location">
                  <Form.Label className="sr-only">Location</Form.Label>
                  <Form.Control
                    name="location"
                    type="input"
                    placeholder="Location"
                    onChange={handleChange}
                    value={form.location}
                  />
                </Form.Group>
                <Button
                  block
                  type="submit"
                  disabled={!isFormValid()}
                  className="mt-5"
                >
                  Next
                </Button>
                <Button
                  onClick={handleBackClick}
                  block
                  variant="link"
                  className="text-muted mt-0 back-button"
                >
                  <small>Back.</small>
                </Button>
              </form>
            </Card.Body>
          </Card>
        </Col>
        <Col className="d-none d-md-flex" md={1} lg={2} xl={3} />
      </Row>
    </Container>
  );
}

export default SignUpAuthorDetails;
